import React, { useState } from 'react';

const Todo = ({ todo, toggleComplete, deleteTodo, editTodo }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [newText, setNewText] = useState(todo.text);
  const [newPriority, setNewPriority] = useState(todo.priority);
  const [newDueDate, setNewDueDate] = useState(todo.dueDate ? todo.dueDate.split('T')[0] : '');

  const handleSave = () => {
    if (newText.trim()) {
      console.log("Sending update:", {
        text: newText,
        priority: newPriority,
        dueDate: newDueDate
      });

      editTodo(todo._id, {
        text: newText,
        priority: newPriority,
        dueDate: newDueDate
      });

      setIsEditing(false);
    }
  };

  return (
    <div className={`todo-card ${todo.completed ? 'completed' : ''}`}>
      {isEditing ? (
        <div className="todo-edit">
          <input
            value={newText}
            onChange={(e) => setNewText(e.target.value)}
          />
          <select
            value={newPriority}
            onChange={(e) => setNewPriority(e.target.value)}
          >
            <option value="Low">Low ðŸ”µ</option>
            <option value="Medium">Medium ðŸŸ¡</option>
            <option value="High">High ðŸ”´</option>
          </select>
          <input
            type="date"
            value={newDueDate}
            onChange={(e) => setNewDueDate(e.target.value)}
          />
          <div className="todo-buttons">
            <button onClick={handleSave}>Save</button>
            <button onClick={() => setIsEditing(false)}>Cancel</button>
          </div>
        </div>
      ) : (
        <div className="todo-display">
          <span className="todo-text" onClick={() => toggleComplete(todo._id, todo.completed)}>
            {todo.text} ({todo.priority}) {todo.dueDate ? `- Due: ${new Date(todo.dueDate).toLocaleDateString()}` : ''}
          </span>
          <div className="todo-buttons">
            <button onClick={() => setIsEditing(true)}>Edit</button>
            <button onClick={() => deleteTodo(todo._id)}>Delete</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Todo;
