// api.js
import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api', // backend
});

// This will run before every request
API.interceptors.request.use((req) => {
  const token = localStorage.getItem('token');
  if (token) {
    req.headers.Authorization = `Bearer ${token}`; // ðŸ‘ˆ required for authMiddleware to work
  }
  return req;
});

export default API;
