import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Todo from './Todo'; // Import the new Todo component

const TodoApp = () => {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState('');

  const fetchTodos = async () => {
    const res = await axios.get('http://localhost:5000/api/todos');
    setTodos(res.data);
  };

  const addTodo = async () => {
    if (!text.trim()) return;
    await axios.post('http://localhost:5000/api/todos', { text });
    setText('');
    fetchTodos();
  };

  const toggleComplete = async (id) => {
    await axios.put(`http://localhost:5000/api/todos/${id}`);
    fetchTodos();
  };

  const deleteTodo = async (id) => {
    await axios.delete(`http://localhost:5000/api/todos/${id}`);
    fetchTodos();
  };

  // New function to handle editing todos
  const editTodo = async (id, newText) => {
    if (!newText.trim()) return;
    await axios.put(`http://localhost:5000/api/todos/${id}`, { text: newText });
    fetchTodos();
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>üìù Todo App</h1>
      <input
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Enter new todo"
      />
      <button onClick={addTodo}>Add</button>
      <div style={{ marginTop: '20px' }}>
        {todos.map(todo => (
          <Todo
            key={todo._id}
            todo={todo}
            toggleComplete={toggleComplete}
            deleteTodo={deleteTodo}
            editTodo={editTodo}  // pass editTodo here
          />
        ))}
      </div>
    </div>
  );
};

export default TodoApp;
