import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Todo from './Todo';
import './Todo.css';

const TodoApp = () => {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState('');
  const [priority, setPriority] = useState('Medium');
  const [dueDate, setDueDate] = useState('');
  const [filter, setFilter] = useState('all');
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    fetchTodos();
  }, []);

  useEffect(() => {
    document.body.className = darkMode ? 'dark' : '';
  }, [darkMode]);

  const fetchTodos = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/todos');
      setTodos(res.data);
    } catch (error) {
      console.error('Failed to fetch todos', error);
    }
  };

  const addTodo = async () => {
    if (!text.trim()) return;
    try {
      await axios.post('http://localhost:5000/api/todos', {
        text,
        priority,
        dueDate: dueDate ? new Date(dueDate) : null,
      });
      setText('');
      setPriority('Medium');
      setDueDate('');
      fetchTodos();
    } catch (error) {
      console.error('Failed to add todo', error);
    }
  };

  const toggleComplete = async (id, completed) => {
    try {
      await axios.put(`http://localhost:5000/api/todos/${id}`, { completed: !completed });
      fetchTodos();
    } catch (error) {
      console.error('Failed to toggle todo', error);
    }
  };

  const deleteTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/todos/${id}`);
      fetchTodos();
    } catch (error) {
      console.error('Failed to delete todo', error);
    }
  };

  const editTodo = async (id, newText) => {
    if (!newText.trim()) return;
    try {
      await axios.put(`http://localhost:5000/api/todos/${id}`, { text: newText });
      fetchTodos();
    } catch (error) {
      console.error('Failed to edit todo', error);
    }
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'completed') return todo.completed;
    if (filter === 'pending') return !todo.completed;
    return true;
  });

  return (
    <div className={`todo-app ${darkMode ? 'dark' : ''}`}>
      <h1>📝 Todo App</h1>

      <button className="mode-toggle" onClick={() => setDarkMode(!darkMode)}>
        {darkMode ? '☀️ Light Mode' : '🌙 Dark Mode'}
      </button>

      <div className="todo-input">
        <input
          type="text"
          value={text}
          placeholder="Enter new todo"
          onChange={e => setText(e.target.value)}
          onKeyDown={e => {
            if (e.key === 'Enter') addTodo();
          }}
        />

        <select value={priority} onChange={e => setPriority(e.target.value)}>
          <option value="Low">Low Priority</option>
          <option value="Medium">Medium Priority</option>
          <option value="High">High Priority</option>
        </select>

        <input
          type="date"
          value={dueDate}
          onChange={e => setDueDate(e.target.value)}
        />

        <button onClick={addTodo} disabled={!text.trim()}>
          Add
        </button>
      </div>

      <div className="filters">
        <button
          className={filter === 'all' ? 'active' : ''}
          onClick={() => setFilter('all')}
        >
          All
        </button>
        <button
          className={filter === 'completed' ? 'active' : ''}
          onClick={() => setFilter('completed')}
        >
          Completed
        </button>
        <button
          className={filter === 'pending' ? 'active' : ''}
          onClick={() => setFilter('pending')}
        >
          Pending
        </button>
      </div>

      <div className="todo-list">
        {filteredTodos.map(todo => (
          <Todo
            key={todo._id}
            todo={todo}
            toggleComplete={toggleComplete}
            deleteTodo={deleteTodo}
            editTodo={editTodo}
          />
        ))}
      </div>
    </div>
  );
};

export default TodoApp;
