import React, { useState } from "react";

const Todo = ({ todo, toggleComplete, deleteTodo, editTodo }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [newText, setNewText] = useState(todo.text);
  const [newPriority, setNewPriority] = useState(todo.priority || "Low");

  // Extract stored date + time separately
  const [newDueDate, setNewDueDate] = useState(
    todo.dueDate ? todo.dueDate.split("T")[0] : ""
  );
  const [newDueTime, setNewDueTime] = useState(
    todo.dueDate && todo.dueDate.includes("T")
      ? todo.dueDate.split("T")[1].slice(0, 5) // HH:MM
      : ""
  );

  const handleSave = () => {
    if (!newText.trim()) return;

    let fullDueDate = null;
    if (newDueDate) {
      fullDueDate = newDueTime
        ? `${newDueDate}T${newDueTime}` // ‚úÖ no Z ‚Üí keeps local time
        : `${newDueDate}T00:00`;
    }

    const updatedTodo = {
      text: newText.trim(),
      priority: newPriority,
      dueDate: fullDueDate || null,
    };

    console.log("‚úèÔ∏è Updating todo:", todo._id, updatedTodo);
    editTodo(todo._id, updatedTodo);
    setIsEditing(false);
  };

  return (
    <div
      className={`todo-item ${todo.completed ? "completed" : ""} ${
        todo.priority ? todo.priority.toLowerCase() : ""
      }`}
      onClick={() => {
        if (isEditing) return; // prevent toggle while editing
        if (!todo.completed) {
          toggleComplete(todo._id, todo.completed);
        }
      }}
      style={{
        cursor: todo.completed ? "not-allowed" : "pointer",
        opacity: todo.completed ? 0.6 : 1,
      }}
    >
      {isEditing ? (
        <>
          <input
            value={newText}
            onChange={(e) => setNewText(e.target.value)}
            onClick={(e) => e.stopPropagation()}
          />

          <select
            value={newPriority}
            onChange={(e) => setNewPriority(e.target.value)}
            onClick={(e) => e.stopPropagation()}
          >
            <option value="Low">Low üîµ</option>
            <option value="Medium">Medium üü°</option>
            <option value="High">High üî¥</option>
          </select>

          <input
            type="date"
            value={newDueDate}
            onChange={(e) => setNewDueDate(e.target.value)}
            onClick={(e) => e.stopPropagation()}
          />

          <input
            type="time"
            value={newDueTime}
            onChange={(e) => setNewDueTime(e.target.value)}
            onClick={(e) => e.stopPropagation()}
          />

          <button onClick={(e) => { e.stopPropagation(); handleSave(); }}>
            Save
          </button>
          <button onClick={(e) => { e.stopPropagation(); setIsEditing(false); }}>
            Cancel
          </button>
        </>
      ) : (
        <>
          <span>
            {todo.text} ({todo.priority}){" "}
            {todo.dueDate
              ? `- Due: ${new Date(todo.dueDate).toLocaleDateString()} ${
                  new Date(todo.dueDate).toLocaleTimeString([], {
                    hour: "2-digit",
                    minute: "2-digit",
                  })
                }`
              : ""}
          </span>

          <button onClick={(e) => { e.stopPropagation(); setIsEditing(true); }}>
            Edit
          </button>
          <button onClick={(e) => { e.stopPropagation(); deleteTodo(todo._id); }}>
            Delete
          </button>
        </>
      )}
    </div>
  );
};

export default Todo;
