import React, { useState } from "react";

const Todo = ({ todo, toggleComplete, deleteTodo, editTodo }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [newText, setNewText] = useState(todo.text);
  const [newPriority, setNewPriority] = useState(todo.priority || "Low");

  // Extract stored date + time separately
  const [newDueDate, setNewDueDate] = useState(
    todo.dueDate ? todo.dueDate.split("T")[0] : ""
  );
  const [newDueTime, setNewDueTime] = useState(
    todo.dueDate && todo.dueDate.includes("T")
      ? todo.dueDate.split("T")[1].slice(0, 5) // HH:mm
      : ""
  );

  const handleSave = () => {
    if (!newText.trim()) return;

    let fullDueDate = null;
    if (newDueDate) {
      fullDueDate = newDueTime
        ? `${newDueDate}T${newDueTime}`
        : `${newDueDate}T00:00`;
    }

    const updatedTodo = {
      text: newText.trim(),
      priority: newPriority,
      dueDate: fullDueDate || null, // ✅ stored as plain string
    };

    console.log("✏️ Updating todo:", todo._id, updatedTodo);
    editTodo(todo._id, updatedTodo);
    setIsEditing(false);
  };

  // ✅ Format date/time manually (no Date object)
  const formatDueDate = (dueDateStr) => {
    if (!dueDateStr) return "";
    const [datePart, timePart] = dueDateStr.split("T");
    if (!datePart) return "";
    const formattedDate = datePart.split("-").reverse().join("-"); // dd-mm-yyyy
    const formattedTime = timePart ? timePart.slice(0, 5) : "00:00";
    return `${formattedDate} ${formattedTime}`;
  };

  return (
    <div
      className={`todo-item ${todo.completed ? "completed" : ""} ${
        todo.priority ? todo.priority.toLowerCase() : ""
      }`}
      onClick={() => {
        if (isEditing) return; // prevent toggle while editing
        if (!todo.completed) {
          toggleComplete(todo._id, todo.completed);
        }
      }}
      style={{
        cursor: todo.completed ? "not-allowed" : "pointer",
        opacity: todo.completed ? 0.6 : 1,
      }}
    >
      {isEditing ? (
        <>
          <input
            value={newText}
            onChange={(e) => setNewText(e.target.value)}
            onClick={(e) => e.stopPropagation()}
          />

          <select
            value={newPriority}
            onChange={(e) => setNewPriority(e.target.value)}
            onClick={(e) => e.stopPropagation()}
          >
            <option value="Low">Low 🔵</option>
            <option value="Medium">Medium 🟡</option>
            <option value="High">High 🔴</option>
          </select>

          <input
            type="date"
            value={newDueDate}
            onChange={(e) => setNewDueDate(e.target.value)}
            onClick={(e) => e.stopPropagation()}
          />

          <input
            type="time"
            value={newDueTime}
            onChange={(e) => setNewDueTime(e.target.value)}
            onClick={(e) => e.stopPropagation()}
          />

          <button
            onClick={(e) => {
              e.stopPropagation();
              handleSave();
            }}
          >
            Save
          </button>
          <button
            onClick={(e) => {
              e.stopPropagation();
              setIsEditing(false);
            }}
          >
            Cancel
          </button>
        </>
      ) : (
        <>
          <span>
            {todo.text} ({todo.priority}){" "}
            {todo.dueDate ? `- Due: ${formatDueDate(todo.dueDate)}` : ""}
          </span>

          <button
            onClick={(e) => {
              e.stopPropagation();
              setIsEditing(true);
            }}
          >
            Edit
          </button>
          <button
            onClick={(e) => {
              e.stopPropagation();
              deleteTodo(todo._id);
            }}
          >
            Delete
          </button>
        </>
      )}
    </div>
  );
};

export default Todo;
