import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';
import TodoApp from './TodoApp';
import Login from './components/Login';
import Signup from './components/Signup';

const App = () => {
  const [authMode, setAuthMode] = useState('login');
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem('token'));

  const handleAuthSuccess = () => setIsAuthenticated(true);
  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsAuthenticated(false);
  };

  if (!isAuthenticated) {
    return (
      <div>
        {authMode === 'login' ? (
          <>
            <Login onAuthSuccess={handleAuthSuccess} />
            <p>Don't have an account? <button onClick={() => setAuthMode('signup')}>Sign up</button></p>
          </>
        ) : (
          <>
            <Signup 
              onAuthSuccess={() => {
                setAuthMode('login');  // switch to login form
                alert('Signup successful! Please login now.');
            }} 
          />
            <p>Already have an account? <button onClick={() => setAuthMode('login')}>Log in</button></p>
          </>
        )}
      </div>
    );
  }

  return (
    <div>
      <button onClick={handleLogout} style={{ float: 'right' }}>ðŸšª Logout</button>
      <TodoApp />
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
