import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Todo from './Todo';
import './Todo.css';



// Set token for all axios requests
axios.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});


const TodoApp = () => {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState('');
  const [priority, setPriority] = useState('Medium');
  const [dueDate, setDueDate] = useState('');
  const [filter, setFilter] = useState('all');
  const [darkMode, setDarkMode] = useState(false);
  const [authError, setAuthError] = useState('');

  useEffect(() => {
    fetchTodos();
  }, []);

  useEffect(() => {
    document.body.className = darkMode ? 'dark' : '';
  }, [darkMode]);

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    return { headers: { Authorization: `Bearer ${token}` } };
  };

  const fetchTodos = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/todos', getAuthHeaders());
      setTodos(res.data);
      setAuthError('');
    } catch (err) {
      console.error(err);
      setAuthError('⚠️ You must be logged in to view your todos.');
    }
  };

  const addTodo = async () => {
    if (!text.trim()) return;
    try {
      await axios.post('http://localhost:5000/api/todos', {
        text,
        priority,
        dueDate
      }, getAuthHeaders());
      setText('');
      setPriority('Medium');
      setDueDate('');
      fetchTodos();
    } catch (err) {
      console.error(err);
      setAuthError('⚠️ Failed to add todo. Please login.');
    }
  };

  const toggleComplete = async (id, completed) => {
    try {
      await axios.put(`http://localhost:5000/api/todos/${id}`, {
        completed: !completed
      }, getAuthHeaders());
      fetchTodos();
    } catch (err) {
      setAuthError('⚠️ Action failed. Please login.');
    }
  };

  const deleteTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/todos/${id}`, getAuthHeaders());
      fetchTodos();
    } catch (err) {
      setAuthError('⚠️ Delete failed. Please login.');
    }
  };

  const editTodo = async (id, updatedFields) => {
    if (updatedFields.text && !updatedFields.text.trim()) return;
    try {
      await axios.put(`http://localhost:5000/api/todos/${id}`, updatedFields, getAuthHeaders());
      fetchTodos();
    } catch (err) {
      setAuthError('⚠️ Edit failed. Please login.');
    }
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'completed') return todo.completed;
    if (filter === 'pending') return !todo.completed;
    return true;
  });

  // ✅ Logout function
  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login'; // Prevent back nav
  };

  return (
    <div className="todo-app">
      {/* ✅ Logout button */}
      <button onClick={handleLogout} style={{ float: 'right', margin: '1rem' }}>
        🚪 Logout
      </button>

      <h1>📝 Todo App</h1>

      <button className="theme-toggle" onClick={() => setDarkMode(!darkMode)}>
        {darkMode ? '☀️ Light Mode' : '🌙 Dark Mode'}
      </button>

      {authError && <p className="auth-error">{authError}</p>}

      <div className="todo-input">
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && addTodo()}
          placeholder="Enter new todo"
        />

        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="Low">Low 🔵</option>
          <option value="Medium">Medium 🟡</option>
          <option value="High">High 🔴</option>
        </select>

        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />

        <button onClick={addTodo}>Add</button>
      </div>

      <div className="filters">
        <button onClick={() => setFilter('all')}>All</button>
        <button onClick={() => setFilter('completed')}>Completed ✅</button>
        <button onClick={() => setFilter('pending')}>Pending ⏳</button>
      </div>

      <div className="todo-list">
        {filteredTodos.map(todo => (
          <Todo
            key={todo._id}
            todo={todo}
            toggleComplete={toggleComplete}
            deleteTodo={deleteTodo}
            editTodo={editTodo}
          />
        ))}
      </div>
    </div>
  );
};

export default TodoApp;
